import React , {useState, useEffect} from 'react'
// import Back from './back.jpg';
import './Card.css';


const Card = () => {
  const [animeData, setAnimeData] = useState([]);
  const apiUrl = 'https://anime-facts-rest-api.herokuapp.com/api/v1';

  useEffect(()=>{
    fetch(apiUrl)
    
    .then((response)=> response.json())
    .then((data) => setAnimeData(data))
    .catch((error)=>console.error('Error fetching data:', error));
  },[]);


  return (

    <div className="outer-box">

    {animeData.map((anime, anime_id)=>(
      <div className='box'>
        <img className='image' src={anime.image} alt={anime.name} />
        <div className='desc-box'>
          <h3 className='title'>{anime.name}</h3>
          <div className='underline'></div>
          <p className='desc'>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Odit, delectus?</p>
        </div>
        <div className='underline'></div>
        <button className='btn' onClick='/App'>View More</button>

        
      </div>

      ))}
    </div>

  )
}

export default Card
